import React, { Component } from 'react';
import { recipeData } from "../data/tempDetails";
import { Link } from "react-router-dom";



class SingleRecipe extends Component {
    constructor(props) {
        super(props);
        const id = this.props.match.params.id;
        this.state = {
            recipe:recipeData,
            id,
            loading:true
        }
    }
    render() {
        const { image_url, publisher, publisher_url,source_url,title,ingridients } = this.state.recipe

        if(this.state.loading)
        {
            return(
                <div className="container">
                    <div className="row">
                        <div className="col mx-auto col-md-6 my-3">
                            <h2 className="text-uppercase text-orange text-center">
                                loading recipe
                            </h2>
                        </div>
                    </div>
                </div>
            )
        }
        return (
            
                <h1>Hello From single recpies</h1>
            
        );
    }
}

export default SingleRecipe;
